mapper.select:
SELECT ROWALL.*,ROWNUM FROM (SELECT ROW_.*, ROWNUM AS NUM_ FROM (
select a.quotaid, a.quotacode, a.postid, a.budgettype, a.budgetnumber, a.budgetdate, a.state, a.memo, a.modiuser, a.moditimestamp, a.modimemo, a.processinstanceid from h_o_quota a,h_o_company b,h_o_department c,h_o_post d
order by a.moditimestamp desc
) ROW_ WHERE ROWNUM <=#{pageInfo.endRowNum}) ROWALL WHERE NUM_ >#{pageInfo.startRowNum}

select quotaid, quotacode, postid, budgettype, budgetnumber, budgetdate, state, memo, modiuser, moditimestamp, modimemo from h_o_quota where 1=1
and valid=#{valid}
order by quotacode

select quotaid, quotacode, postid, budgettype, budgetnumber, budgetdate, state, memo, modiuser, moditimestamp, modimemo from h_o_quota where state=#{state}
and postid=#{postid}
and budgettype=#{budgettype}
and quotaid != #{quotaid}

select decode(sum(t.OperateNum),null,0,sum(t.OperateNum)) as operatenum from J_RecruitProgramOperate t where
t.recruitprogramguid=#{recruitprogramguid}
and t.operatestate=3

SELECT ROWALL.*,ROWNUM FROM (SELECT ROW_.*, ROWNUM AS NUM_ FROM (
select quotaoperateguid, quotaid, operatestate, modiuser, moditimestamp, modimemo, operatenum from j_quotaoperate
order by moditimestamp
) ROW_ WHERE ROWNUM <=#{pageInfo.endRowNum}) ROWALL WHERE NUM_ >#{pageInfo.startRowNum}
